sap.ui.define(['sap/ui/webc/common/thirdparty/base/renderer/LitRenderer'], function (litRender) { 'use strict';

	const block0 = (context, tags, suffix) => litRender.html`<div class="ui5-switch-root ${litRender.classMap(context.classes.main)}" role="checkbox" aria-checked="${litRender.ifDefined(context.checked)}" aria-disabled="${litRender.ifDefined(context.ariaDisabled)}" aria-labelledby="${litRender.ifDefined(context._id)}-hiddenText" @click="${context._onclick}" @keyup="${context._onkeyup}" @keydown="${context._onkeydown}" tabindex="${litRender.ifDefined(context.tabIndex)}" dir="${litRender.ifDefined(context.effectiveDir)}"><div class="ui5-switch-inner"><div class="ui5-switch-track" part="slider"><div class="ui5-switch-slider">${ context.graphical ? block1(context, tags, suffix) : block2(context) }<span class="ui5-switch-handle" part="handle"></span></div></div></div><input type='checkbox' ?checked="${context.checked}" class="ui5-switch-input" data-sap-no-tab-ref/><span id="${litRender.ifDefined(context._id)}-hiddenText" class="ui5-hidden-text">${litRender.ifDefined(context.hiddenText)}</span></div>`;
	const block1 = (context, tags, suffix) => litRender.html`<span class="ui5-switch-text ui5-switch-text--on"><${litRender.scopeTag("ui5-icon", tags, suffix)} name="accept" dir="ltr" class="ui5-switch-icon-on"></${litRender.scopeTag("ui5-icon", tags, suffix)}></span><span class="ui5-switch-text ui5-switch-text--off"><${litRender.scopeTag("ui5-icon", tags, suffix)} name="decline" class="ui5-switch-icon-off"></${litRender.scopeTag("ui5-icon", tags, suffix)}></span>`;
	const block2 = (context, tags, suffix) => litRender.html`<span class="ui5-switch-text ui5-switch-text--on" part="text-on">${litRender.ifDefined(context._textOn)}</span><span class="ui5-switch-text ui5-switch-text--off" part="text-off">${litRender.ifDefined(context._textOff)}</span>`;

	return block0;

});
